package com.zxxapp.mall.maintenance.helper.alipay;import java.util.Map;import android.text.TextUtils;public class PayResult {	private String resultStatus;	private String result;	private String memo;	private String out_trade_no;	public PayResult(Map<String, String> rawResult) {		if (rawResult == null) {			return;		}		for (String key : rawResult.keySet()) {			if (TextUtils.equals(key, "resultStatus")) {				resultStatus = rawResult.get(key);			} else if (TextUtils.equals(key, "result")) {				result = rawResult.get(key);			} else if (TextUtils.equals(key, "memo")) {				memo = rawResult.get(key);			}			else if (TextUtils.equals(key, "out_trade_no")) {				out_trade_no = rawResult.get(key);			}		}	}	@Override	public String toString() {		return "resultStatus={" + resultStatus + "};memo={" + memo				+ "};result={" + result + "}";	}	/**	 * @return the resultStatus	 */	public String getResultStatus() {		return resultStatus;	}	/**	 * @return the memo	 */	public String getMemo() {		return memo;	}	public String getOutTradeNo() {		return out_trade_no;	}	/**	 * @return the result	 */	public String getResult() {		return result;	}}